pl.DataFrame({
    "int": [1, 2],
    "str": ["a", "b"],
    "bool": [True, None],
    "list": [[1, 2], [3]]
}).select([
    pl.struct(pl.all()).alias("my_struct")  # this takes all columns and combines them into Series of type Struct
]).to_series().to_list() == [(1, "a", True, pl.Series([1, 2])), (2, "b", None, pl.Series([3]))]  # convert to a python list of tuples
